import React from 'react';
import { View, Text } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Icon from 'react-native-vector-icons/Ionicons';
import HomeScreenBottom from './HomeScreenBottom';
import ProfileScreenBottom from './ProfileScreenBottom';

const Tab = createBottomTabNavigator();

function BottomTabNavigator() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={({ route }) => ({
          tabBarIcon: ({ color, size }) => {
            let iconName;
            if (route.name === 'Home') {
              iconName = 'home';
            } else if (route.name === 'Profile') {
              iconName = 'person-circle';
            }
            return <Icon name={iconName} size={size} color={color} />;
          },
          tabBarLabel: ({ focused, color }) => {
            let labelName;
            if (route.name === 'Home') {
              labelName = 'Home';
            } else if (route.name === 'Profile') {
              labelName = 'Profile';
            }
            return (
              <View style={{ alignItems: 'center' }}>
                <Icon name={iconName} size={focused ? 30 : 25} color={color} />
                <Text style={{ color: color, fontSize: 12 }}>{labelName}</Text>
              </View>
            );
          },
        })}
        tabBarOptions={{
          activeTintColor: 'blue',
          inactiveTintColor: 'gray',
          style: {
            backgroundColor: 'white',
            borderTopWidth: 0,
            elevation: 0,
          },
        }}>
        <Tab.Screen name="Home" component={HomeScreenBottom} />
        <Tab.Screen name="Profile" component={ProfileScreenBottom} />
      </Tab.Navigator>
    </NavigationContainer>
  );
}

export default BottomTabNavigator;
