import React, { useState } from 'react';
import { View, TextInput } from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';

function SearchBar() {
  const [text, setText] = useState('');

  return (
    <View style={{ flexDirection: 'row', alignItems: 'center', backgroundColor: '#f1f1f1', borderRadius: 10, paddingHorizontal: 10 }}>
      <Icon name="search-outline" size={20} color="#888" style={{ marginRight: 5 }} />
      <TextInput
        style={{ flex: 1, height: 40 }}
        placeholder="Search"
        onChangeText={text => setText(text)}
        value={text}
      />
    </View>
  );
}

export default SearchBar;
